import React from "react";
import Navigation from "../components/Navigation";
import axios from "axios";
import { useState } from "react";

const Home = () => {
  const [name, setName] = useState("");
  const [prenom, setPrenom] = useState("");
  const [age, setAge] = useState("");
  const [sexe, setSexe] = useState("Homme");

  const postData = () => {
    const post = {
      method: "POST", // Get =>Apporte moi données // POST (incremente donées), PUT (MAJ), DELETE (Supprimer données)
      headers: { "Content-Type": "application/json" },
      mode: "cors",
      body: JSON.stringify({
        nom: name,
        prenom: prenom,
        sexe: sexe,
        age: age,
      }),
      cache: "default",
    };
    axios
      .get("http://localhost:8000/back/api/create", post)
      .then((response) => {
        console.log(response.data);
      });
  };

  return (
    <div>
      <Navigation />

      <h1>Ajout Eleve</h1>

      <form action="" className="form-create">
        <label for="name">Nom </label>
        <input
          id="name"
          type="text"
          placeholder="Entrez votre nom"
          onChange={(e) => {
            setName(e.target.value);
          }}
        ></input>
        <label for="prenom">Prenom </label>
        <input
          id="prenom"
          type="text"
          placeholder="Entrez votre prenom"
          onChange={(e) => {
            setPrenom(e.target.value);
          }}
        ></input>
        <label for="age">Age</label>
        <input
          id="age"
          type="number"
          min="0"
          max="99"
          step="1"
          value="18"
          onChange={(e) => {
            setAge(e.target.value);
          }}
        ></input>
        <label for="gender">Genre</label>
        <select
          id="gender"
          onChange={(e) => {
            setSexe(e.target.value);
          }}
        >
          <option value="homme">Homme</option>
          <option value="femme">Femme</option>
        </select>
      </form>
      <button className="Validation" onClick={postData}>
        Valider
      </button>
    </div>
  );
};

export default Home;
